var hasFilled = false;
var hasClickedLogin = false;
var hasClicked4 = false;
var hasClicked2 = false;
var hasClicked3 = false;
var scriptStopped = false;

function randomDelay(min, max) {
    return Math.floor(Math.random() * (max - min + 1)) + min;
}

function simulateHumanBehavior() {
    var randomScroll = Math.random() > 0.7;
    if (randomScroll) {
        window.scrollTo(0, randomDelay(100, 300));
    }
}

function hideAutomation() {
    Object.defineProperty(navigator, 'webdriver', {
        get: function() { return false; }
    });

    Object.defineProperty(navigator, 'plugins', {
        get: function() { return [1, 2, 3, 4, 5]; }
    });

    Object.defineProperty(navigator, 'languages', {
        get: function() { return ['zh-CN', 'zh', 'en']; }
    });

    window.chrome = { runtime: {} };
}

document.addEventListener('DOMContentLoaded', function() {
    hideAutomation();
    autoFillLogin();
});

function autoFillLogin() {
    var targetUrl = 'https://www.qsxtiahyzx.com';

    function fill() {
        if (scriptStopped || window.location.href.indexOf(targetUrl) === -1) {
            return;
        }

        if (hasFilled && hasClickedLogin) {
            return;
        }

        var usernameInput = document.querySelector("#app > div > form > div:nth-child(1) > div > div.el-input.el-input--suffix > input");
        var passwordInput = document.querySelector("#app > div > form > div:nth-child(2) > div > div:nth-child(1) > div > input");
        var loginButton = document.querySelector("#app > div > span > button:nth-child(1) > span");

        if (usernameInput && passwordInput && loginButton) {
            if (!hasFilled) {
                usernameInput.value = 'To-700';
                usernameInput.dispatchEvent(new Event('input', { bubbles: true }));
                usernameInput.dispatchEvent(new Event('change', { bubbles: true }));

                setTimeout(function() {
                    passwordInput.value = 'B96GppB75hUw';
                    passwordInput.dispatchEvent(new Event('input', { bubbles: true }));
                    passwordInput.dispatchEvent(new Event('change', { bubbles: true }));

                    hasFilled = true;

                    setTimeout(function() {
                        if (hasFilled && !hasClickedLogin) {
                            loginButton.click();
                            hasClickedLogin = true;
                        }
                    }, randomDelay(100, 300));
                }, randomDelay(100, 250));
            }
        } else {
            setTimeout(fill, 500);
        }
    }

    setTimeout(fill, randomDelay(800, 1200));

    setInterval(function() {
        if (scriptStopped) return;

        simulateHumanBehavior();

        if (!hasFilled || !hasClickedLogin) {
            fill();
        }

        checkAndClickBtn4();
        checkAndClickBtn2();
        checkBtn3AndStop();
    }, 100);
}

function findByXPath(xpath) {
    try {
        var result = document.evaluate(xpath, document, null, XPathResult.FIRST_ORDERED_NODE_TYPE, null);
        return result.singleNodeValue;
    } catch (e) {
        return null;
    }
}

function checkAndClickBtn4() {
    if (scriptStopped || hasClicked4) {
        return;
    }

    var xpath = "//button[contains(@class,'el-button--primary')]//span[contains(text(),'领取今日任务')]";
    var btn = findByXPath(xpath);

    if (btn && btn.parentElement) {
        var delay = randomDelay(50, 150);
        setTimeout(function() {
            btn.parentElement.click();
            hasClicked4 = true;
        }, delay);
    }
}

function checkAndClickBtn2() {
    if (scriptStopped || !hasClicked4 || hasClicked2) {
        return;
    }

    var xpath = "//button[contains(@class,'el-button--primary')]//span[contains(text(),'确认并拉取任务')]";
    var btn = findByXPath(xpath);

    if (btn && btn.parentElement) {
        var delay = randomDelay(50, 150);
        setTimeout(function() {
            btn.parentElement.click();
            hasClicked2 = true;
        }, delay);
    }
}

function checkBtn3AndStop() {
    if (scriptStopped) {
        return;
    }

    if (!hasClicked2) {
        return;
    }

    var xpath3 = "//button[contains(@class,'el-button--primary')]//span[contains(text(),'重新获取任务')]";
    var btn3 = findByXPath(xpath3);

    if (btn3 && btn3.parentElement && !hasClicked3) {
        var delay3 = randomDelay(50, 150);
        setTimeout(function() {
            btn3.parentElement.click();
            hasClicked3 = true;
        }, delay3);
        return;
    }

    var errorText = findByXPath("//span[contains(text(),'响应码异常:3,参数异常')]");

    if (errorText) {
        var backBtn = document.querySelector("#app > section > main > div > button");
        if (backBtn) {
            setTimeout(function() {
                backBtn.click();
            }, randomDelay(30, 80));

            var checkConfirm = setInterval(function() {
                var btn = document.querySelector("body > div.el-message-box__wrapper > div > div.el-message-box__btns > button.el-button.el-button--default.el-button--small.el-button--primary");
                if (btn) {
                    clearInterval(checkConfirm);
                    setTimeout(function() {
                        btn.click();

                        hasClicked4 = false;
                        hasClicked2 = false;
                        hasClicked3 = false;
                    }, randomDelay(30, 80));
                }
            }, 50);
        }
        return;
    }

    var xpath4 = "//button[contains(@class,'el-button--primary')]//span[contains(text(),'开始执行任务')]";
    var btn4 = findByXPath(xpath4);

    if (btn4) {
        scriptStopped = true;
        return;
    }

    if (hasClicked3) {
        hasClicked3 = false;
    }
}
